function doGet() {
  var userEmail = Session.getActiveUser().getEmail();

  // Verificar si el correo está en la hoja de cálculo
  var sheetId = "1It-BrcNukUGFyJHl0KaMs2i36RUPf3b6OxSduLDrZbU"; // Reemplaza con el ID de tu hoja de cálculo
  var sheet = SpreadsheetApp.openById(sheetId).getSheetByName("User");
  var emails = sheet.getRange("B:B").getValues();

  var hasAccess = false;

  for (var i = 0; i < emails.length; i++) {
    if (emails[i][0] === userEmail) {
      hasAccess = true;
      break;
    }
  }

  if (hasAccess) {
    var html = HtmlService.createTemplateFromFile("index");
    var response = html.evaluate()
      .setTitle("IAN BOLIVAR")
      .setXFrameOptionsMode(HtmlService.XFrameOptionsMode.ALLOWALL)
      .addMetaTag("viewport", "width=device-width, initial-scale=1");

    // Establecer el favicon
    var faviconUrl = 'https://itgocek.com/Asistencia%20Bolivar/Logo_IAN_BOLIVAR.png'; // Reemplaza con la URL directa de tu imagen
    response.setFaviconUrl(faviconUrl);

    return response;
  } else {
    var html = HtmlService.createHtmlOutput('<h1>No tienes permisos para acceder a esta aplicación.</h1>')
      .setTitle("Acceso Denegado")
      .setXFrameOptionsMode(HtmlService.XFrameOptionsMode.ALLOWALL);
    console.log("Usuario: " + userEmail)
    return html;
  }
}

