function incluirArchivo(pagina){

  var html = HtmlService.createTemplateFromFile(pagina);

  return html.evaluate().getContent();


}


/*============================================================

   ==================== notificacion Soporte  ==========================

==============================================================*/

function enviarSoporteControl(form){


  var html = HtmlService.createHtmlOutputFromFile("plantillaSoporte").getContent();

  var fecha = new Date();
  var dia = fecha.getDate();
  var mes = fecha.getMonth()+1;
  var year = fecha.getFullYear();

  html = html.replace("|*tipoSoporte*|", form.itemSoporte);
  html = html.replace("|*descripcionSoporte*|", form.descripcionSoporte);
  html = html.replace("|*dia*|", dia);
  html = html.replace("|*mes*|", mes);
  html = html.replace("|*year*|", year);

  GmailApp.sendEmail("luis.gomez@segurosbolivar.com", "Solicitud Soporte IAN BOLIVAR", html, {htmlBody:html} );

  return "OkSoporte"

}

    /*============================================================

   ==================== Controlador de acciones ==========================

==============================================================*/

function controlador(form, accion, id){

  Logger.log(form)
  if(form!=""){

    accion = form.txtAccion;
    Logger.log(accion)
  }
  Logger.log("La accion es " + accion)
  try{

      switch(accion){

          case "OfertasServicios":
            return OfertasServicios(id)
            break;

          case "OfertasServiciosAntioquia":
          return OfertasServiciosAntioquia(id)
          break;


          case "OfertasServiciosCali":
          return OfertasServiciosCali(id)
          break;

          case "OfertasServiciosEjeCafetero":
          return OfertasServiciosEjeCafetero(id)
          break;

          case "OfertasServiciosBolivar":
          return OfertasServiciosBolivar(id)
          break;

          case "OfertasServiciosAtlantico":
          return OfertasServiciosAtlantico(id)
          break;

          case "OfertasAceptadas":
          return OfertasAceptadas(id)
          break;

          case "editarTodoslosCasos":
          Logger.log("Llegamos al case")
          return editarTodosCasos(form)
          break;

          case "editarEditarMisCasosProveedor":
          return editarEditarMisCasosProveedor(form)
          break;

          case "OfertasNoAceptadas":
          return OfertasNoAceptadas(id)
          break;

          case "editarTodoslosCasosNoAceptados":
          Logger.log("Llegamos al case")
          return editarTodosCasosNoAceptados(form)
          break;

          case "MisOFertas":
          return MisOFertas(id)
          break;

          case "rol":
          return Rol()
          break;

          case "editarEnviaraGOElegidos":
          return enviarGo(form)
          break;

          case "cancelarGOElegidos":
          return cancelarGo(form); 
          break;

          case "tarifaDinamica":
          return tarifaDinamica(id);
          break;

          case "enviarTarifaDinamica":
          return enviarTarifaDinamica(form)
          break;

          case "editarTarifaDinamica":
          return editarTarifaDinamica(form)
          break;

          case "eliminarTarifaDinamica":
          return eliminarTarifaDinamica(form)
          break;

          case "eventos":
          return eventos(id)
          break;

          case "DisponibilidadProveedor":
          return DisponibilidadProveedor(id)
          break;

          case "crearDisponibilidad":
          return crearDisponibilidad(form)
          break;

          case "editarDisponibilidad":
          return editarDisponibilidad(form)
          break;

          case "eliminarDisponibilidad":
          return eliminarDisponibilidad(form)
          break;


      }

  }catch(e){

    return "Error en el Controlador" + e

  }

}



