function OfertasServicios() {
  var libro = SpreadsheetApp.openById("1It-BrcNukUGFyJHl0KaMs2i36RUPf3b6OxSduLDrZbU");
  var hoja = libro.getSheetByName("Data");
  var hojaTecnicos = libro.getSheetByName("Tecnicos");
  var hojaTarifas = libro.getSheetByName("Tarifas");

  var fila = hoja.getLastRow();
  var datos = hoja.getRange("A2:AM" + fila).getDisplayValues();

  var datosTecnicos = hojaTecnicos.getRange("A2:C" + hojaTecnicos.getLastRow()).getValues();

  var datosTarifas = hojaTarifas.getRange("B2:H" + hojaTarifas.getLastRow()).getDisplayValues();

  var resultado = [];
  var timeZone = libro.getSpreadsheetTimeZone();
  var correoActual = Session.getActiveUser().getEmail().trim().toLowerCase();

  var empresa = null;
  var tecnicos = [];

  // Filtrar técnicos según el correo del usuario
  for (var j = 0; j < datosTecnicos.length; j++) {
    if (datosTecnicos[j][0].trim().toLowerCase() === correoActual) {
      if (!empresa) {
        empresa = datosTecnicos[j][1];
      }
      var tecnico = datosTecnicos[j][2].trim();
      if (tecnico && tecnicos.indexOf(tecnico) === -1) {
        tecnicos.push(tecnico);
      }
    }
  }

  // Si no se encontró la empresa, establecer "No disponible"
  if (!empresa) {
    empresa = 'No disponible';
  }

  // Recorrer los datos de la hoja "Data"
  for (var i = 0; i < datos.length; i++) {
    if (datos[i][26] == "OF" && (datos[i][36] == "CUNDINAMARCA" || datos[i][36] == "DISTRITO CAPITAL")) {
      var fechaProgramado = datos[i][11];
      var horaProgramado = datos[i][12];

      // Formatear fecha y hora
      if (fechaProgramado instanceof Date) {
        fechaProgramado = Utilities.formatDate(fechaProgramado, timeZone, "dd/MM/yyyy");
      }

      if (horaProgramado instanceof Date) {
        horaProgramado = Utilities.formatDate(horaProgramado, timeZone, "HH:mm:ss");
      } else {
        horaProgramado = formatTime(horaProgramado);
      }

      // Arreglo para almacenar las tarifas filtradas
      var tarifas = []; 

      // Filtrar tarifas de "CUNDINAMARCA" y donde la columna H no sea "C"
      for (var k = 0; k < datosTarifas.length; k++) {
        if (datosTarifas[k][5] == "CUNDINAMARCA" && datosTarifas[k][6] == "A") { 
          tarifas.push({
            tarifaB: datosTarifas[k][0], // Columna B
            tarifaC: datosTarifas[k][1], // Columna C
            tarifaD: datosTarifas[k][2], // Columna D
            tarifaE: datosTarifas[k][3], // Columna E
            tarifaF: datosTarifas[k][4]  // Columna F
          });
        }
      }

      // Convertir tarifas a un formato de texto para mostrar
      var tarifasTexto = tarifas.map(t => 
        `B: ${t.tarifaB}, C: ${t.tarifaC}, D: ${t.tarifaD}, E: ${t.tarifaE}, F: ${t.tarifaF}`
      ).join('; ');

      // Agregar al resultado
      resultado.push({
        id: datos[i][25],
        origen: datos[i][7],
        direccionOrigen: datos[i][9],
        destino: datos[i][8],
        direccionDestino: datos[i][10],
        fechaProgramado: fechaProgramado,
        horaProgramado: horaProgramado,
        valorDinamico:datos[i][38],
        acciones: "<div class='checkbox-wrapper-31'>" +
            "<input type='checkbox' class='mycheck2' id='check_" + datos[i][25] + "' onclick='handleCheckboxClick()'>" +
            "<svg viewBox='0 0 35.6 35.6'>" +
              "<circle class='background' cx='17.8' cy='17.8' r='17.8'></circle>" +
              "<circle class='stroke' cx='17.8' cy='17.8' r='14.37'></circle>" +
              "<polyline class='check' points='11.78 18.12 15.55 22.23 25.17 12.87'></polyline>" +
            "</svg>" +
          "</div>",
        empresa: empresa,
        tecnicos: tecnicos,
        tarifas: tarifasTexto// Tarifa con filtro aplicado

      });
    }
  }

  console.log(resultado);
  return resultado;
}



