function actualizarDatosEnHoja(datos) {
  var libro = SpreadsheetApp.openById("1It-BrcNukUGFyJHl0KaMs2i36RUPf3b6OxSduLDrZbU");
  var hoja = libro.getSheetByName("Data");

  var ciudad; // Variable para almacenar la ciudad

  // Primero, verifica si todos los datos están en el estado "OF" en la columna AA
  var todosEnEstadoOF = datos.every(function(oferta) {
    var fila = buscarFilaPorId(oferta.id, hoja);
    if (fila !== -1) {
      var estadoActual = hoja.getRange("AA" + fila).getValue(); // Columna AA es la 27
      ciudad = hoja.getRange("AK" + fila).getValue(); // Guarda la ciudad (Columna AK)
      return estadoActual === "OF";
    }
    return false; // Si no encuentra la fila, considera que no está en el estado correcto
  });

  if (!todosEnEstadoOF) {
    return { exito: false, mensaje: "No se puede actualizar, al menos uno de los elementos no está en el estado 'OF'", ciudad: ciudad };
  }

  var resultado = {}; // Definir resultado fuera del bucle para evitar ReferenceError

  // Si todos los elementos están en el estado "OF", procede a actualizar
  datos.forEach(function(oferta) {
    var fila = buscarFilaPorId(oferta.id, hoja); 

    if (fila !== -1) {
      hoja.getRange("AA" + fila).setValue("TM");
      hoja.getRange("AE" + fila).setValue("PENDIENTE POR ASIGNAR");
      hoja.getRange("AB" + fila).setValue(oferta.proveedor);
      //hoja.getRange("AC" + fila).setValue(oferta.tecnico);
       // Obtén la fecha y hora actuales
      var now = new Date();
      // Formatea la fecha y hora en el formato deseado
      var formattedDate = Utilities.formatDate(now, Session.getScriptTimeZone(), "MM/dd/yyyy HH:mm:ss");
      // Establece el valor formateado en la celda
      hoja.getRange("AD" + fila).setValue(formattedDate);  // Hora actual
      // Obtén el valor de la columna AK después de actualizar la fila
      resultado.ciudad = hoja.getRange("AK" + fila).getValue(); 
    }
  });

  return { exito: true, mensaje: "Datos actualizados correctamente", ciudad: resultado.ciudad || ciudad || null };
}


function buscarFilaPorId(id, hoja) {
  var rangoIds = hoja.getRange("Z2:Z" + hoja.getLastRow()); // Asumiendo que el ID está en la columna Z
  var valores = rangoIds.getValues();
  for (var i = 0; i < valores.length; i++) {
    if (valores[i][0] === id) {
      return i + 2;  // Ajuste del índice para compensar encabezado
    }
  }
  return -1;  // Si no se encuentra, retornamos -1
}






function formatTime(value) {
  // Si el valor es un número, convertirlo al formato "HH:mm:ss"
  if (typeof value === 'number') {
    var totalMinutes = Math.round(value * 24 * 60);
    var hours = Math.floor(totalMinutes / 60);
    var minutes = totalMinutes % 60;
    var seconds = Math.round((value * 24 * 60 - totalMinutes) * 60);

    // Asegurarse de que siempre tenga dos dígitos
    return ('0' + hours).slice(-2) + ':' + ('0' + minutes).slice(-2) + ':' + ('0' + seconds).slice(-2);
  }
  return value;
}


