function obtenerProveedoresYTecnicos() {
  var libro = SpreadsheetApp.openById("1It-BrcNukUGFyJHl0KaMs2i36RUPf3b6OxSduLDrZbU");
  var hojaTecnicos = libro.getSheetByName("Tecnicos");
  const datos = hojaTecnicos.getDataRange().getValues();

  const proveedores = {};

  // Suponiendo que la primera fila contiene los encabezados
  for (let i = 1; i < datos.length; i++) {
    const empresa = datos[i][1];   // Columna B
    const tecnico = datos[i][2];   // Columna C

    if (!proveedores[empresa]) {
      proveedores[empresa] = [];
    }

    if (!proveedores[empresa].includes(tecnico)) {
      proveedores[empresa].push(tecnico);
    }
  }

  return {
    proveedores: Object.keys(proveedores),
    tecnicosPorProveedor: proveedores
