// Función para obtener coordenadas utilizando la API de Google Maps Geocoding
function obtenerCoordenadasGoogleMaps(direccion) {
  var apiKey = 'AIzaSyA5gMP900tBB-MKyOpNTJ2BytXNLLN-i9s';  // Reemplaza con tu propia clave de API de Google Maps
  var apiEndpoint = 'https://maps.googleapis.com/maps/api/geocode/json';

  var url = apiEndpoint + '?address=' + encodeURIComponent(direccion) + '&key=' + apiKey;

  try {
    var response = UrlFetchApp.fetch(url);
    var responseData = JSON.parse(response.getContentText());

    if (responseData.status === 'OK' && responseData.results.length > 0) {
      var location = responseData.results[0].geometry.location;
      return { latitud: location.lat, longitud: location.lng };
    } else {
      console.error('No se encontraron coordenadas para la dirección:', direccion);
      return null;
    }
  } catch (error) {
    console.error('Error al obtener coordenadas:', error);
    return null;
  }
}


