    if (!isNaN(originalDate.getTime()) && title != "") {
      // Ajustamos la fecha de inicio al día siguiente
      var startDate = new Date(originalDate);
      startDate.setDate(startDate.getDate() + 1);  // Incrementa el día
      startDate.setHours(0, 0, 0, 0);              // Establece la hora al inicio del día

